+#ifdef NPY_HAVE_NEON
+#    define EINSUM_IS_ALIGNED(x) 0
+#else  // NPY_HAVE_NEON
+#    define EINSUM_IS_ALIGNED(x) npy_is_aligned(x, NPY_SIMD_WIDTH)
+#endif  // NPY_HAVE_NEON
